template <class T>
void slv::flag::ISSUE(FlagType _flag_type, std::string warning_message, const T& warning_arg) {
    std::ostringstream stream;
    stream << warning_intro() << warning_message << warning_arg << std::endl;
    ISSUE(_flag_type, stream);
}
